import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

}
<p>Inicio Sesión</p>

<div>
  <input inputmode="email" class="emailBox" placeholder="Email" />
</div>
<div>
  <input type="password" class="pwdBox" placeholder="Password" />
</div>
<div>
  <a routerLink="/createAccount">Crear Cuenta</a>
</div>
<div>
  <button>Ingresar</button>
</div>
